package ch2;

public class CharToCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'A';
		int code = (int)ch;
		
		System.out.printf("%c = %d %n", ch, code);
		
		System.out.println('\'');
		System.out.println('\n');
		System.out.println('\'');
		System.out.println("\"Hello\"");
		System.out.println("c:\\");
	}

}
